Verilog<pre><code>module top_module( input in, output out );
    assign out=in;
endmodule
</code></pre>

Verilog<pre><code>module top_module( 
    input a,b,c,
    output w,x,y,z );
    assign {w,x,y,z} = {a,b,b,c};
endmodule
</code></pre> <ul> <li>If we're certain about the width of each signal, using the concatenation operator is equivalent and shorter: assign {w,x,y,z} = {a,b,b,c};</li> </ul>

Verilog<pre><code>module top_module( input in, output out );

endmodule
</code></pre>

Verilog<pre><code>module top_module( input in, output out );
    assign out=~in;
endmodule
</code></pre> <ul> <li>"~"意味着按照位来取反</li> <li>"!"意味着逻辑取反</li> </ul>

Verilog<pre><code>`default_nettype none
module top_module(
    input a,
    input b,
    input c,
    input d,
    output out,
    output out_n   ); 
    wire wire1,wire2;
    assign wire1=a&amp;b;
    assign wire2=c&amp;d;
    assign out=wire1|wire2;
    assign out_n=~out;
endmodule
</code></pre>

Verilog<pre><code>module top_module ( 
    input p1a, p1b, p1c, p1d, p1e, p1f,
    output p1y,
    input p2a, p2b, p2c, p2d,
    output p2y );
    assign p1y=p1a&amp;p1b&amp;p1c|p1d&amp;p1e&amp;p1f;
    assign p2y=p2a&amp;p2b|p2c&amp;p2d;
endmodule
</code></pre>

Verilog<pre><code>module top_module ( input a, input b, output out );
    mod_a inst (a,b,out);   // By position
    // mod_a inst (.in1(a), .in2(b), .out(out));        By name
endmodule
</code></pre>

Verilog<pre><code>module top_module ( input clk, input d, output q );
    wire w1,w2;
    my_dff inst1 (clk,d,w1);
    my_dff inst2 (clk,w1,w2);
    my_dff inst3 (clk,w2,q);
endmodule
</code></pre>

Verilog<pre><code>module top_module ( 
    input clk, 
    input [7:0] d, 
    input [1:0] sel, 
    output [7:0] q 
);
    wire  [7:0] w1, w2, w3;
    my_dff8 inst1 (clk, d, w1);
    my_dff8 inst2 (clk, w1, w2);
    my_dff8 inst3 (clk, w2, w3);
    always @* begin
        case(sel)
            0: q = d;
            1: q = w1;
            2: q = w2;
            3: q = w3;
        endcase
    end
endmodule
</code></pre> <ul> <li>第7行中的定义方式是将w1,w2,w3全部定义成了8位</li> </ul> <p>若要在一行中定义不一样位数的变量，可以使用以下方式</p> Verilog<pre><code>wire  [7:0] w1; wire w2;
</code></pre> <p>其中，w1是8位的，w2是1位的</p>

Verilog<pre><code>module top_module(
    input [31:0] a,
    input [31:0] b,
    output [31:0] sum
);
    wire w1;
    add16 inst1 (a[15:0],b[15:0],0,sum[15:0],w1);
    add16 inst2 (a[31:16],b[31:16],w1,sum[31:16]);
endmodule
</code></pre>

Verilog<pre><code>module top_module (
    input [31:0] a,
    input [31:0] b,
    output [31:0] sum
);
    wire w1;
    add16 inst1 (a[15:0],b[15:0],0,sum[15:0],w1);
    add16 inst2 (a[31:16],b[31:16],w1,sum[31:16]);
endmodule

module add1 ( input a, input b, input cin,   output sum, output cout );
    assign sum=a^b^cin;
    assign cout=a&amp;b|a&amp;cin|b&amp;cin;
endmodule
</code></pre>

Verilog<pre><code>module top_module(
    input [31:0] a,
    input [31:0] b,
    output [31:0] sum
);
    wire sel;
    wire [15:0] sum_0,sum_1;
    add16 inst1 (a[15:0],b[15:0],0,sum[15:0],sel);
    add16 inst2 (a[31:16],b[31:16],0,sum_0);
    add16 inst3 (a[31:16],b[31:16],1,sum_1);
    always @* begin
        case(sel)
            0: sum[31:16]=sum_0;
            1: sum[31:16]=sum_1;
        endcase
    end
endmodule
</code></pre> <ul> <li>请注意always语句的结构</li> </ul>

Verilog<pre><code>module top_module(
    input [31:0] a,
    input [31:0] b,
    input sub,
    output [31:0] sum
);
    wire [31:0] b_sub;
    wire cout1;
    assign b_sub=b^{32{sub}};
    add16 inst1 (a[15:0],b_sub[15:0],sub,sum[15:0],cout1);
    add16 inst2 (a[31:16],b_sub[31:16],cout1,sum[31:16]);
endmodule
</code></pre>

Verilog<pre><code>module top_module (
    input [7:0] a, b, c, d,
    output [7:0] min);//
    wire [7:0] w1,w2;
    assign w1=a&gt;b?b:a;
    assign w2=c&gt;d?d:c;
    assign min=w1&gt;w2?w2:w1;
endmodule
</code></pre>

Verilog<pre><code>module top_module (
    input [7:0] in,
    output parity); 
    assign parity=^in;
endmodule
</code></pre>

Verilog<pre><code>module top_module( 
    input [99:0] in,
    output [99:0] out
);
    always @(*) begin
        for (int i=0;i&lt;$bits(out);i++)
            out[i]=in[$bits(out)-i-1];
    end
endmodule
</code></pre> <ul> <li>$bits() is a system function that returns the width of a signal.</li> <li>$bits(out) is 100 because out is 100 bits wide.</li> </ul>

Verilog<pre><code>module top_module( 
    input [254:0] in,
    output [7:0] out );
    always @(*) begin
        out=0;
        for (int i=0;i&lt;$bits(in);i++)
            out=out+in[i];  // out+=in[i]
    end
endmodule
</code></pre> <ul> <li>注意需要对out初始化</li> </ul>

Verilog<pre><code>module top_module( 
    input [99:0] a, b,
    input cin,
    output [99:0] cout,
    output [99:0] sum );
    always @(*) begin
        {cout[0],sum[0]}=a[0]+b[0]+cin;
        for (int i=1;i&lt;$bits(cout);i++)
            {cout[i],sum[i]}=a[i]+b[i]+cout[i-1];
    end
endmodule
</code></pre>

Verilog<pre><code>module top_module( 
    input [399:0] a, b,
    input cin,
    output cout,
    output [399:0] sum );
    wire [99:0] w1;
    assign cout=w1[99];
    genvar i;
    generate
        for (i=0;i&lt;100;i++) begin:block1
            if (i==0) begin
                bcd_fadd inst (a[i*4+3:i*4],b[i*4+3:i*4],cin,w1[i],sum[i*4+3:i*4]);
            end
            else begin
                bcd_fadd inst (a[i*4+3:i*4],b[i*4+3:i*4],w1[i-1],w1[i],sum[i*4+3:i*4]);
            end
        end
     endgenerate
endmodule
</code></pre> <ul> <li>for循环后面的那个模块名称是必要的</li> <li>genvar是一种特殊的数据类型，特别用于generate for语句</li> </ul>

Verilog<pre><code>// synthesis verilog_input_version verilog_2001
module top_module(
    input a, 
    input b,
    output wire out_assign,
    output reg out_alwaysblock
);
    assign out_assign=a&amp;b;
    always @(*) begin
        out_alwaysblock=a&amp;b;
    end
endmodule
</code></pre>

Verilog<pre><code>// synthesis verilog_input_version verilog_2001
module top_module(
    input clk,
    input a,
    input b,
    output wire out_assign,
    output reg out_always_comb,
    output reg out_always_ff   );
    assign out_assign=a^b;
    always @(*) out_always_comb=a^b;
    always @(posedge clk) out_always_ff&lt;=a^b;
endmodule
</code></pre>

Verilog<pre><code>// synthesis verilog_input_version verilog_2001
module top_module ( 
    input [2:0] sel, 
    input [3:0] data0,
    input [3:0] data1,
    input [3:0] data2,
    input [3:0] data3,
    input [3:0] data4,
    input [3:0] data5,
    output reg [3:0] out   );//

    always@(*) begin  // This is a combinational circuit
        case(sel)
            0: out=data0;
            1: out=data1;
            2: out=data2;
            3: out=data3;
            4: out=data4;
            5: out=data5;
            default: out=0;
        endcase 
    end
endmodule
</code></pre>

Verilog<pre><code>// synthesis verilog_input_version verilog_2001
module top_module (
    input [3:0] in,
    output reg [1:0] pos  );
    always @(*) begin
        case (in)
            4'b0000:pos=0;
            4'b0001:pos=2'd0;
            4'b0010:pos=2'd1;
            4'b0011:pos=2'd0;
            4'b0100:pos=2'd2;
            4'b0101:pos=2'd0;
            4'b0110:pos=2'd1;
            4'b0111:pos=2'd0;
            4'b1000:pos=2'd3;
            4'b1001:pos=2'd0;
            4'b1010:pos=2'd1;
            4'b1011:pos=2'd0;
            4'b1100:pos=2'd2;
            4'b1101:pos=2'd0;
            4'b1110:pos=2'd1;
            4'b1111:pos=2'd0;
        endcase
    end
endmodule
</code></pre> <ul> <li>There is an easier way to code this. See the next problem (always_casez).</li> </ul>

Verilog<pre><code>// synthesis verilog_input_version verilog_2001
module top_module (
    input [7:0] in,
    output reg [2:0] pos );
    always @(*) begin
        casez (in)
            8'bzzzz_zzz1: pos=0;
            8'bzzzz_zz1z: pos=1;
            8'bzzzz_z1zz: pos=2;
            8'bzzzz_1zzz: pos=3;
            8'bzzz1_zzzz: pos=4;
            8'bzz1z_zzzz: pos=5;
            8'bz1zz_zzzz: pos=6;
            8'b1zzz_zzzz: pos=7;
            8'b0000_0000: pos=0;
        endcase
    end
endmodule
</code></pre> <ul> <li>Notice that you should use <code>casez (input)</code> if you want to use casez statement. The letter z is  neccessary.</li> </ul>

Verilog<pre><code>// synthesis verilog_input_version verilog_2001
module top_module (
    input [15:0] scancode,
    output reg left,
    output reg down,
    output reg right,
    output reg up  ); 
    always @(*) begin
        up = 1'b0; down = 1'b0; left = 1'b0; right = 1'b0;
        case (scancode)
            16'he06b: left=1;
            16'he072: down=1;
            16'he074: right=1;
            16'he075: up=1;
        endcase
    end
endmodule
</code></pre>

Verilog<pre><code>module top_module ( 
    input wire [2:0] vec,
    output wire [2:0] outv,
    output wire o2,
    output wire o1,
    output wire o0  ); // Module body starts after module declaration
    assign outv=vec,o0=vec[0],o1=vec[1],o2=vec[2];
    //  This is ok too: assign {o2, o1, o0} = vec;
endmodule
</code></pre>

Verilog<pre><code>`default_nettype none     // Disable implicit nets. Reduces some types of bugs.
module top_module( 
    input wire [15:0] in,
    output wire [7:0] out_hi,
    output wire [7:0] out_lo );
    assign {out_hi,out_lo}=in;
    // Also: assign out_hi = in[15:8];
    //       assign out_lo = in[7:0];
endmodule
</code></pre>

Verilog<pre><code>module top_module( 
    input [31:0] in,
    output [31:0] out );
    assign out={in[7:0],in[15:8],in[23:16],in[31:24]};
endmodule
</code></pre>

Verilog<pre><code>module top_module( 
    input [2:0] a,
    input [2:0] b,
    output [2:0] out_or_bitwise,
    output out_or_logical,
    output [5:0] out_not
);
    assign out_or_bitwise=a|b;
    assign out_or_logical=a||b;
    assign out_not={~b,~a};
endmodule
</code></pre>

Verilog<pre><code>module top_module( 
    input [3:0] in,
    output out_and,
    output out_or,
    output out_xor
);
    assign out_and=in[3]&amp;in[2]&amp;in[1]&amp;in[0];
    assign out_or=in[3]|in[2]|in[1]|in[0];
    assign out_xor=in[3]^in[2]^in[1]^in[0];
endmodule
</code></pre>

Verilog<pre><code>module top_module (
    input [4:0] a, b, c, d, e, f,
    output [7:0] w, x, y, z );
    assign {w,x,y,z}={a,b,c,d,e,f,2'b11};
endmodule
</code></pre>

Verilog<pre><code>module top_module( 
    input [7:0] in,
    output [7:0] out
);
    assign out={in[0],in[1],in[2],in[3],in[4],in[5],in[6],in[7]};
endmodule
</code></pre>

Verilog<pre><code>module top_module (
    input [7:0] in,
    output [31:0] out );
    assign out={{24{in[7]}},in};
endmodule
</code></pre> <ul> <li>尤其需要注意的是，{num{vector}}只有在左右都加上括号的情况下才是一个向量</li> </ul>

Verilog<pre><code>module top_module (
    input a, b, c, d, e,
    output [24:0] out );
    assign out=~{{5{a}},{5{b}},{5{c}},{5{d}},{5{e}}}^{5{a,b,c,d,e}};
endmodule
</code></pre>

